import React, {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from "axios";
import { TextField } from "@material-ui/core";
import {Button, Form} from 'react-bootstrap';
import Select from 'react-select';
import countryList from "react-select-country-list";

export default function Planner(props) {
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [country, setCountry] = useState(null);
    const [holiday, setHolidays] = useState("");
    const [result, setResult] = useState("");
    const [displayResult, setDisplayResult] = useState(false);

    const options = countryList().getData();


    const changeHandler = country => {
        console.log("country ", country);
        setCountry(country)
    };

    const sendData = () => {
        axios
        .post(
        "http://localhost:8080/api/dates?startDate=" +
        startDate +
        "&endDate=" +
        endDate + "&country=" + country.label,
        {
            headers: {
                "Access-Control-Allow-Origin": "*",
                "Access-Control-Allow-Methods": "*",
                "Access-Control-Allow-Headers": "*",
                "Content-Type": "application/text",
            },
        }
        )
        .then((response) => {

            console.log("response ", response);
            setHolidays(response.data);
        })
        .catch(function (error) {
            console.log("error", error);
        });
    };

    const onSubmit = (event) => {
        event.preventDefault();
        sendData();
        setDisplayResult(true);

    };

    return (
    <div>
        <Form onSubmit={onSubmit}>
            <Select
            options={options}
            value={country}
            onChange={changeHandler}
            />
            <TextField
            type="date"
            defaultValue={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            inputProps={{ min: "2020-04-01", max: "2021-03-31" }}
            />
            <TextField
            type="date"
            defaultValue={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            inputProps={{ min: startDate, max: "2021-03-31" }}
            />
            <Button variant="dark">Submit</Button>
        </Form>
        <text>The person should take {holiday} holiday days</text>
    </div>
    );
}
