import React, {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import styles from "./index.css";
import axios from "axios";
import { TextField } from "@material-ui/core";
import Select from 'react-select';
import countryList from "react-select-country-list";

export default function Planner(props) {
    const [startDate, setStartDate] = useState(null);
    const [endDate, setEndDate] = useState(null);
    const [country, setCountry] = useState(null);
    const [holiday, setHolidays] = useState("");
    const [result, setResult] = useState("");
    const [displayResult, setDisplayResult] = useState(false);

    const options = countryList().getData();

    const customStyles = {
        option: (provided, state) => ({
            ...provided,
            borderBottom: '1px dotted pink',
            color: state.isSelected ? 'red' : 'blue',
            padding: 20,
        }),
        control: () => ({
            // none of react-select's styles are passed to <Control />
            width: 263,
        }),
        singleValue: (provided, state) => {
            const opacity = state.isDisabled ? 0.5 : 1;
            const transition = 'opacity 300ms';

            return { ...provided, opacity, transition };
        }
    };


    const changeHandler = country => {
        console.log("country ", country);
        setCountry(country)
    };

    const sendData = () => {
        if (startDate && endDate && country) {

        axios
        .post(
        "http://localhost:8080/api/dates?startDate=" +
        startDate +
        "&endDate=" +
        endDate + "&country=" + country.value,
        {
            headers: {
                "Access-Control-Allow-Origin": "*",
                "Access-Control-Allow-Methods": "*",
                "Access-Control-Allow-Headers": "*",
                "Content-Type": "application/text",
            },
        }
        )
        .then((response) => {

            console.log("response ", response);
            setHolidays(response.data);
        })
        .catch(function (error) {
            console.log("error", error);
        });
        }
        else {
            console.log("Some required params are missing!");
        }
    };

    const onSubmit = (event) => {
        event.preventDefault();
        sendData();
        setDisplayResult(true);

    };

    return (
    <div>
        <form onSubmit={onSubmit}>
            <Select
                styles={customStyles}
            options={options}
            value={country}
            onChange={changeHandler}
            />
            <TextField
            type="date"
            defaultValue={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            inputProps={{ min: "2020-04-01", max: "2021-03-31" }}
            />
            <TextField
            type="date"
            defaultValue={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            inputProps={{ min: startDate, max: "2021-03-31" }}
            />
            <button>Submit</button>
        </form>
        <text>The person should take {holiday} holiday days</text>
    </div>
    );
}
